import {
  QueryClient,
  QueryClientProvider,
  useQuery,
  useQueryClient,
} from "react-query";
import { ErrorBoundary } from "react-error-boundary";

const queryClient = new QueryClient();

export default function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <Example />
    </QueryClientProvider>
  );
}

let url = "https://api.github.co/repos/tannerlinsley/react-query";

const query = () => {
  return fetch(url).then((res) => res.json());
};

function Example() {
  return (
    <ErrorBoundary
      FallbackComponent={() => <div>ok</div>}
      onError={() => console.log("poof!")}
    >
      <Fetcher />
    </ErrorBoundary>
  );
}

const Fetcher = () => {
  const queryClient = useQueryClient();

  // console.log(queryClient.queryCache.queriesMap);

  if (queryClient.queryCache.queriesMap) {
    console.log(queryClient.queryCache);
  }

  const { isLoading, data } = useQuery("repoData", query, {
    useErrorBoundary: true,
  });

  if (isLoading) return "Loading...";

  return (
    <div>
      <h1>{data.name}</h1>
      <p>{data.description}</p>
      <strong>üëÄ {data.subscribers_count}</strong>{" "}
      <strong>‚ú® {data.stargazers_count}</strong>{" "}
      <strong>üç¥ {data.forks_count}</strong>
    </div>
  );
};
